            clearInterval(esperarPaginaCarregar);
            return;
        }

        if (localStorage.getItem("teste-1348")) {
            clearInterval(esperarPaginaCarregar);
            return;
        }

        const cardsMelhoresOfertasExistem = Array.from(document.querySelectorAll(".cont-destaque-carousel ngu-item button[data-gtm-event-action='clique:eu-quero']"));

        if (cardsMelhoresOfertasExistem.length === 0) return;

        clearInterval(esperarPaginaCarregar);

        setTimeout(() => {
            const cep = sessionStorage.getItem("cep");
            const number = sessionStorage.getItem("number");

            const link = document.createElement("a");
            link.href = `https://planos.claro.com.br/novo/checkout?cep=${cep}&number=${number}`;
            link.style.display = "none";
            link.setAttribute("id", "link-trigger");
            document.body.appendChild(link);

            const cardsMelhoresOfertas = Array.from(document.querySelectorAll(".cont-destaque-carousel ngu-item"));

            const cardsFiltrados = cardsMelhoresOfertas.filter((card) => {
                const conteudoCard = Array.from(card.querySelectorAll("span"));

                // some(): De dentro da lista, ele ir verificar se algum item cumpre a condio da funo. Se sim, retorna true; se no, retorna false.
                return !conteudoCard.some((titulo) => {
                    return titulo.textContent.includes("GB") || titulo.textContent.includes("App Claro");
                });
            });

            cardsFiltrados.forEach((card, index) => {
                const btn = document.querySelector(`#cro-btn-${index}`);

                if (btn) btn.remove();

                const botaoEuQuero = card.querySelector(`button:not(#cro-btn-${index})`);

                const botaoClonado = botaoEuQuero.cloneNode(true);
                botaoClonado.setAttribute("id", `cro-btn-${index}`);

                botaoEuQuero.insertAdjacentElement("afterend", botaoClonado);
                botaoEuQuero.style.display = "none";

                botaoClonado.addEventListener("click", () => {
                    executeTrigger();

                    setTimeout(() => {
                        botaoEuQuero.click();
                    }, 620);
                });
            });

        }, 1000);

    }, 200);



















})()
