            (function() {
    vwo_$(document).ready(function () {

        let carrinhoValidado = null;
        let buttonFillData = null;
        let newElement = null;
        let bottomMobileButton = null;

        const intervalCRO = setInterval(() => {
            if (window.location.pathname != "/monte-sua-combinacao") clearInterval(intervalCRO);

            if (buttonFillData && bottomMobileButton) {
                clearInterval(intervalCRO);
                insertNewBottomMobileButton();
                addNewElement();
                return;
            }

            if (!bottomMobileButton) {
                bottomMobileButton = document.querySelector("a[title='Prximo']");
            }

            if (!buttonFillData) {
                const buttons = document.querySelectorAll("button[aria-label='Avanar com sua compra e preencher seus dados']:not(#cro-btn)");
                if (buttons.length === 2) {
                    buttonFillData = window.innerWidth >= 1200 ? buttons[0] : buttons[1];
                }
            }

        }, 250);

        function insertNewBottomMobileButton() {
            const croLink = document.querySelector("#cro-link");
            if (croLink) {
                croLink.addEventListener("click", checkCart);
                return;
            }

            const clone = bottomMobileButton.cloneNode(true);
            clone.setAttribute("id", "cro-link");

            bottomMobileButton.insertAdjacentElement("afterend", clone);
            bottomMobileButton.style.display = "none";

            clone.addEventListener("click", redirect);
        }

        function addNewElement() {
            if (window.location.pathname != '/monte-sua-combinacao') return;

            if (document.querySelector("#cro-btn")) {
                document.querySelector("#cro-btn").addEventListener("click", redirect);
                return;
            }

            newElement = buttonFillData.cloneNode(true);
            newElement.setAttribute("id", "cro-btn");

            buttonFillData.insertAdjacentElement("afterend", newElement);
            buttonFillData.style.display = "none";

            newElement.addEventListener("click", redirect);
        }

        function redirect() {
            if (carrinhoValidado) {
                executeTrigger();
            };

            setTimeout(() => {
                buttonFillData.click();
            }, 400);
        }

        const intervalCaart = setInterval(() => {
            if (window.location.pathname != "/monte-sua-combinacao") clearInterval(intervalCaart);

            const carrinho = document.querySelector("#carrinho-selecao");
            if (!carrinho) return;

            const produtosAdicionados = carrinho.querySelectorAll(".cart-mdn-product-name-topic");
            const botaoPreencher = carrinho.querySelector('button[aria-label*="Avanar com sua compra"]');
            if (!botaoPreencher) return;

            const totalProdutos = produtosAdicionados.length;

            if (totalProdutos === 0) {
                if (buttonFillData) buttonFillData.setAttribute("disabled", "true");
                if (newElement) newElement.setAttribute("disabled", "true");

            } else {
                if (buttonFillData) buttonFillData.removeAttribute("disabled");
                if (newElement) newElement.removeAttribute("disabled");
            }

            const temCelular = [...produtosAdicionados].some(produto =>
                produto.querySelector(".mdn-Icon-celular")
            );

            const temOutrosProdutos = [...produtosAdicionados].some(produto =>
                !produto.querySelector(".mdn-Icon-celular")
            );

            const temAppClaroTV = [...produtosAdicionados].some(produto => {
                const desc = produto.querySelector(".mdn-Topic-description");
                if (!desc) return false;

                const texto = desc.textContent.trim().toLowerCase();
                return texto.includes("app claro tv+");
            });

            carrinhoValidado =
                !temAppClaroTV &&
                (
                    (!temCelular && totalProdutos > 1) ||
                    (!temCelular && temOutrosProdutos)
                );

        }, 250);
    });
})()
