            (function() {
    if (window.location.pathname != "/checkout/personalizacao-movel") return;
    let originalButton = null;

    const waitButtonNext = setInterval(() => {
        if (window.location.pathname != "/checkout/personalizacao-movel") clearInterval(waitButtonNext);

        originalButton = document.querySelector("app-buttons-default#personalizacao-movel-next");

        if (originalButton) {
            clearInterval(waitButtonNext);

            const observer = new MutationObserver(observeChanges);
            observer.observe(originalButton, { attributes: true });

            observeChanges();
        }

    }, 100);

    function observeChanges() {
        const myButton = document.querySelector("#cro-app");

        if (myButton) myButton.remove();

        originalButton = document.querySelector("app-buttons-default#personalizacao-movel-next");

        if (originalButton) {
            const buttonClone = originalButton.cloneNode(true);

            buttonClone.setAttribute("id", "cro-app");
            buttonClone.querySelector("button").setAttribute("id", "cro-btn");

            if (!originalButton.getAttribute("hidden")) {
                originalButton.style.display = "none";
                buttonClone.removeAttribute("style");
            }

            originalButton.insertAdjacentElement("afterend", buttonClone);

            buttonClone.addEventListener("click", validation);
        }
    }

    function validation() {
        const selectedPlan = sessionStorage.getItem("step-personalizacao-movel");
